import React, { useState } from 'react';
import CodeEditor from './components/CodeEditor';
import ChatBox from './components/ChatBox';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [output, setOutput] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
  };

  const handleRunCode = async () => {
    try {
      setIsLoading(true);
      setOutput(null);

      const response = await fetch('http://localhost:5000/api/code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, language }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to execute code');
      }

      setOutput(data);
    } catch (error) {
      console.error('Error running code:', error);
      setOutput({ error: error.message });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChatMessage = (message) => {
    // This will be implemented later when we add AI integration
    console.log('Chat message:', message);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
          <h1 className="text-2xl font-bold text-gray-900">AI Technical Interview Practice</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex gap-6 h-[800px]">
          {/* Left side - Chat */}
          <div className="w-1/2">
            <ChatBox onSendMessage={handleChatMessage} />
          </div>

          {/* Right side - Code Editor */}
          <div className="w-1/2 flex flex-col space-y-4">
            <div className="flex-1 bg-white rounded-lg shadow">
              <CodeEditor
                code={code}
                language={language}
                onCodeChange={handleCodeChange}
                onLanguageChange={handleLanguageChange}
              />
            </div>

            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-semibold text-gray-800">Code Output</h2>
                <button
                  onClick={handleRunCode}
                  disabled={isLoading}
                  className={`px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:opacity-50 ${
                    isLoading ? 'cursor-not-allowed' : ''
                  }`}
                >
                  {isLoading ? 'Running...' : 'Run Code'}
                </button>
              </div>

              {output && (
                <div className="bg-gray-50 rounded-md overflow-x-auto">
                  <pre className="p-4">
                    {output.error ? (
                      <span className="text-red-600">{output.error}</span>
                    ) : (
                      <>
                        {output.stdout && (
                          <div className="mb-2">
                            <div className="whitespace-pre-wrap font-mono text-sm">{output.stdout}</div>
                          </div>
                        )}
                        {output.stderr && (
                          <div className="text-red-600">
                            <div className="whitespace-pre-wrap font-mono text-sm">{output.stderr}</div>
                          </div>
                        )}
                      </>
                    )}
                  </pre>
                </div>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
