import React, { useState } from 'react';
import { useTheme } from '../hooks/useTheme';

const TestCasePanel = ({ testCases, activeTestCase, onTestCaseChange, isDarkMode }) => {
  const [selectedTab, setSelectedTab] = useState(0);
  const { colors } = useTheme(isDarkMode);

  const handleTabClick = (index) => {
    setSelectedTab(index);
    onTestCaseChange(index);
  };

  return (
    <div className="flex flex-col h-full" style={{ backgroundColor: colors.background.secondary }}>
      {/* Test Case Tabs */}
      <div className="flex items-center p-2" style={{ 
        backgroundColor: colors.background.primary,
        borderBottom: `1px solid ${colors.border.primary}`
      }}>
        <div className="flex space-x-1 overflow-x-auto">
          {testCases.map((_, index) => (
            <button
              key={index}
              onClick={() => handleTabClick(index)}
              className="px-4 py-2 rounded-t-lg text-sm font-medium transition-colors duration-150"
              style={{
                backgroundColor: selectedTab === index ? colors.background.tertiary : colors.background.secondary,
                color: selectedTab === index ? colors.text.primary : colors.text.secondary,
              }}
            >
              Case {index + 1}
            </button>
          ))}
          <button
            className="px-4 py-2 rounded-t-lg text-sm font-medium transition-colors duration-150"
            style={{
              backgroundColor: colors.background.secondary,
              color: colors.text.secondary,
            }}
          >
            +
          </button>
        </div>
      </div>

      {/* Test Case Content */}
      <div className="flex-1 p-4 overflow-y-auto">
        {testCases[selectedTab] && (
          <div className="space-y-4">
            <div className="space-y-2">
              <label className="block text-sm font-medium" style={{ color: colors.text.primary }}>
                Input Parameters
              </label>
              <input
                type="text"
                value={testCases[selectedTab].input}
                readOnly
                className="w-full px-3 py-2 rounded-md"
                style={{
                  backgroundColor: colors.background.primary,
                  color: colors.text.primary,
                  border: `1px solid ${colors.border.secondary}`,
                }}
              />
            </div>
            <div className="space-y-2">
              <label className="block text-sm font-medium" style={{ color: colors.text.primary }}>
                Expected Output
              </label>
              <input
                type="text"
                value={testCases[selectedTab].expectedOutput}
                readOnly
                className="w-full px-3 py-2 rounded-md"
                style={{
                  backgroundColor: colors.background.primary,
                  color: colors.text.primary,
                  border: `1px solid ${colors.border.secondary}`,
                }}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TestCasePanel;
