import React, { useState } from 'react';
import CodeEditor from './components/CodeEditor';
import ChatBox from './components/ChatBox';
import Header from './components/Header';
import Footer from './components/Footer';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [output, setOutput] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
  };

  const handleRunCode = async () => {
    try {
      setIsLoading(true);
      setOutput(null);

      // Basic validation
      if (!code || !code.trim()) {
        setOutput({ error: 'Please enter some code to execute' });
        return;
      }

      const response = await fetch('http://localhost:5000/api/code/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ 
          code: code.trim(), 
          language 
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.error || `HTTP error! status: ${response.status}`);
      }

      let data;
      try {
        data = await response.json();
      } catch (error) {
        console.error('Failed to parse response:', error);
        throw new Error('Invalid response from server');
      }

      if (!data || typeof data !== 'object') {
        throw new Error('Invalid response format');
      }

      if (!data.success) {
        setOutput({ 
          error: data.error || 'Failed to execute code',
          details: data.details || null
        });
        return;
      }

      setOutput({
        stdout: data.data.stdout || '',
        stderr: data.data.stderr || '',
        error: data.data.error || null
      });

    } catch (error) {
      console.error('Error running code:', error);
      setOutput({ 
        error: error.message || 'Failed to execute code',
        details: error.details || null
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChatMessage = (message) => {
    // This will be implemented later when we add AI integration
    console.log('Chat message:', message);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900' : 'bg-gray-100'}`}>
      <Header isDarkMode={isDarkMode} toggleTheme={toggleTheme} />

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex gap-6 h-[800px]">
          {/* Left side - Chat */}
          <div className="w-1/2">
            <ChatBox onSendMessage={handleChatMessage} isDarkMode={isDarkMode} />
          </div>

          {/* Right side - Code Editor */}
          <div className="w-1/2 flex flex-col space-y-4">
            <div className={`flex-1 ${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow`}>
              <CodeEditor
                code={code}
                language={language}
                onCodeChange={handleCodeChange}
                onLanguageChange={handleLanguageChange}
                isDarkMode={isDarkMode}
              />
            </div>

            <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow p-4`}>
              <div className="flex justify-between items-center mb-4">
                <h2 className={`text-lg font-semibold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                  Code Output
                </h2>
                <button
                  onClick={handleRunCode}
                  disabled={isLoading}
                  className={`px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:opacity-50 ${
                    isLoading ? 'cursor-not-allowed' : ''
                  }`}
                >
                  {isLoading ? 'Running...' : 'Run Code'}
                </button>
              </div>

              {output && (
                <div className={`${isDarkMode ? 'bg-gray-700' : 'bg-gray-50'} rounded-md overflow-x-auto`}>
                  <pre className="p-4">
                    {output.error ? (
                      <div className="text-red-500">
                        <div className="font-semibold">Error:</div>
                        <div className="whitespace-pre-wrap">{output.error}</div>
                        {output.details && (
                          <div className="mt-2 text-sm opacity-75 whitespace-pre-wrap">
                            {output.details}
                          </div>
                        )}
                      </div>
                    ) : (
                      <>
                        {output.stdout && (
                          <div className="mb-2">
                            <div className={`whitespace-pre-wrap font-mono text-sm ${isDarkMode ? 'text-gray-200' : 'text-gray-800'}`}>
                              {output.stdout}
                            </div>
                          </div>
                        )}
                        {output.stderr && (
                          <div className="text-red-500">
                            <div className="font-semibold">Error Output:</div>
                            <div className="whitespace-pre-wrap font-mono text-sm">{output.stderr}</div>
                          </div>
                        )}
                      </>
                    )}
                  </pre>
                </div>
              )}
            </div>
          </div>
        </div>
      </main>

      <Footer isDarkMode={isDarkMode} />
    </div>
  );
}

export default App;
