import React, { useEffect } from 'react';
import Editor from '@monaco-editor/react';
import TestResults from './TestResults';
import { Box } from '@mui/material';

const SUPPORTED_LANGUAGES = {
  python: 'python',
  javascript: 'javascript',
  java: 'java',
  cpp: 'cpp'
};

const DEFAULT_CODE = {
  python: `# Python Example
def greet(name):
    return f"Hello, {name}!"

# Test the function
print(greet("World"))`,

  javascript: `// JavaScript Example
function greet(name) {
    return \`Hello, \${name}!\`;
}

// Test the function
console.log(greet("World"));`,

  java: `// Java Example
public class Main {
    public static void main(String[] args) {
        System.out.println(greet("World"));
    }
    
    public static String greet(String name) {
        return "Hello, " + name + "!";
    }
}`,

  cpp: `// C++ Example
#include <iostream>
#include <string>

std::string greet(const std::string& name) {
    return "Hello, " + name + "!";
}

int main() {
    std::cout << greet("World") << std::endl;
    return 0;
}`
};

function CodeEditor({ 
  code, 
  language, 
  onCodeChange, 
  isDarkMode = true, 
  functionTemplate,
  testResults = [],
  isSubmission = false
}) {
  useEffect(() => {
    if (functionTemplate && !code) {
      onCodeChange(functionTemplate);
    }
  }, [functionTemplate, code, onCodeChange]);

  const handleEditorChange = (value) => {
    onCodeChange(value);
  };

  const getLanguageConfig = () => {
    switch (language) {
      case 'python':
        return 'python';
      case 'javascript':
        return 'javascript';
      case 'java':
        return 'java';
      case 'cpp':
        return 'cpp';
      default:
        return 'python';
    }
  };

  return (
    <div style={{ 
      height: '100%', 
      display: 'flex', 
      flexDirection: 'column' 
    }}>
      <div style={{ 
        height: testResults?.length > 0 ? '70%' : '100%', 
        borderBottom: testResults?.length > 0 ? '1px solid #404040' : 'none' 
      }}>
        <Editor
          height="100%"
          defaultLanguage={SUPPORTED_LANGUAGES[language] || 'python'}
          value={code}
          theme={isDarkMode ? 'vs-dark' : 'light'}
          onChange={handleEditorChange}
          options={{
            minimap: { enabled: false },
            fontSize: 14,
            lineNumbers: 'on',
            automaticLayout: true,
            scrollBeyondLastLine: false,
            wordWrap: 'on',
            tabSize: 4,
            insertSpaces: true,
          }}
        />
      </div>
      {testResults?.length > 0 && (
        <Box sx={{ 
          height: '30%', 
          overflow: 'auto',
          borderTop: '1px solid #404040',
          bgcolor: '#1a1a1a'
        }}>
          <TestResults 
            results={testResults} 
            isSubmission={isSubmission}
          />
        </Box>
      )}
    </div>
  );
}

export default CodeEditor;
