import React, { useState } from 'react';
import CodeEditor from './components/CodeEditor';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [output, setOutput] = useState(null);

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
  };

  const handleRunCode = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, language }),
      });
      const data = await response.json();
      setOutput(data);
    } catch (error) {
      console.error('Error running code:', error);
      setOutput({ error: 'Failed to execute code' });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">Code Editor</h1>
        </div>
      </header>
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 gap-6">
          <div className="bg-white shadow rounded-lg p-6">
            <CodeEditor
              code={code}
              language={language}
              onCodeChange={handleCodeChange}
              onLanguageChange={handleLanguageChange}
            />
            <div className="mt-4">
              <button
                onClick={handleRunCode}
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              >
                Run Code
              </button>
            </div>
          </div>
          {output && (
            <div className="bg-white shadow rounded-lg p-6">
              <h2 className="text-xl font-semibold mb-4">Output</h2>
              <pre className="bg-gray-100 p-4 rounded-md overflow-x-auto">
                {output.error ? (
                  <span className="text-red-600">{output.error}</span>
                ) : (
                  <>
                    {output.stdout && (
                      <div className="mb-2">
                        <span className="font-semibold">Output:</span>
                        <div className="whitespace-pre-wrap">{output.stdout}</div>
                      </div>
                    )}
                    {output.stderr && (
                      <div className="text-red-600">
                        <span className="font-semibold">Error:</span>
                        <div className="whitespace-pre-wrap">{output.stderr}</div>
                      </div>
                    )}
                  </>
                )}
              </pre>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
