import React, { useState } from 'react';
import Split from 'react-split';
import CodeEditor from './components/CodeEditor';
import ProblemPanel from './components/ProblemPanel';
import Header from './components/Header';
import Footer from './components/Footer';
import './styles/split.css';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [output, setOutput] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [testCases, setTestCases] = useState([]);
  const [hiddenTestCases, setHiddenTestCases] = useState([]);
  const [problemDescription, setProblemDescription] = useState('');
  const [functionTemplate, setFunctionTemplate] = useState('');
  const [activeTestCase, setActiveTestCase] = useState(0); // For running individual test cases

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
    // Update function template based on language
    if (functionTemplate) {
      // This will be handled by the AI to provide language-specific templates
      console.log('Language changed, updating template...');
    }
  };

  const handleChatMessage = async (message) => {
    // This will be implemented with AI integration
    console.log('Chat message:', message);
    // Mock AI response for now
    setProblemDescription('Sample problem description from AI');
    setTestCases([
      { input: 'example input 1', expectedOutput: 'example output 1' },
      { input: 'example input 2', expectedOutput: 'example output 2' }
    ]);
    setHiddenTestCases([
      { input: 'hidden input 1', expectedOutput: 'hidden output 1' },
      { input: 'hidden input 2', expectedOutput: 'hidden output 2' }
    ]);
    setFunctionTemplate(`def solution():
    # Write your code here
    pass`);
  };

  const executeCode = async (testCasesToRun, isSubmission = false) => {
    try {
      setIsLoading(true);
      setOutput(null);

      if (!code || !code.trim()) {
        setOutput({ error: 'Please enter some code to execute' });
        return;
      }

      const response = await fetch('http://localhost:5000/api/code/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ 
          code: code.trim(), 
          language,
          testCases: testCasesToRun,
          isSubmission
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.error || `HTTP error! status: ${response.status}`);
      }

      let data;
      try {
        data = await response.json();
      } catch (error) {
        console.error('Failed to parse response:', error);
        throw new Error('Invalid response from server');
      }

      if (!data || typeof data !== 'object') {
        throw new Error('Invalid response format');
      }

      if (!data.success) {
        setOutput({ 
          error: data.error || 'Failed to execute code',
          details: data.details || null
        });
        return;
      }

      setOutput({
        stdout: data.data.stdout || '',
        stderr: data.data.stderr || '',
        error: data.data.error || null,
        testResults: data.data.testResults || [],
        isSubmission: isSubmission,
        submissionStatus: data.data.submissionStatus,
        runtime: data.data.runtime,
        memory: data.data.memory
      });

    } catch (error) {
      console.error('Error running code:', error);
      setOutput({ 
        error: error.message || 'Failed to execute code',
        details: error.details || null
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleRunTestCase = async () => {
    // Run single test case
    const testCase = testCases[activeTestCase];
    await executeCode([testCase]);
  };

  const handleRunAllTestCases = async () => {
    // Run all visible test cases
    await executeCode(testCases);
  };

  const handleSubmit = async () => {
    // Run all test cases including hidden ones
    await executeCode([...testCases, ...hiddenTestCases], true);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`h-screen flex flex-col ${isDarkMode ? 'bg-[#1E1E1E]' : 'bg-[#FAFAFA]'}`}>
      <Header isDarkMode={isDarkMode} toggleTheme={toggleTheme} />

      <main className="flex-1 px-4 py-2 sm:px-6 lg:px-8">
        <Split 
          className={`flex h-full ${isDarkMode ? 'dark' : ''}`}
          sizes={[40, 60]} 
          minSize={[300, 400]}
          expandToMin={false}
          gutterSize={10}
          gutterAlign="center"
          snapOffset={30}
          dragInterval={1}
          direction="horizontal"
          cursor="col-resize"
        >
          {/* Left side - Problem Description and Chat */}
          <div className="h-full">
            <ProblemPanel
              onSendMessage={handleChatMessage}
              isDarkMode={isDarkMode}
              problemDescription={problemDescription}
              testCases={testCases}
            />
          </div>

          {/* Right side - Code Editor and Test Cases */}
          <div className="h-full">
            <Split
              className="flex flex-col h-full"
              sizes={[65, 35]}
              minSize={[200, 100]}
              expandToMin={false}
              gutterSize={10}
              gutterAlign="center"
              snapOffset={30}
              dragInterval={1}
              direction="vertical"
              cursor="row-resize"
            >
              {/* Code Editor Section */}
              <div className="w-full">
                <div className="flex justify-between items-center mb-2">
                  <div className="flex items-center space-x-4">
                    <select
                      value={language}
                      onChange={(e) => handleLanguageChange(e.target.value)}
                      className={`px-3 py-1 rounded-md ${
                        isDarkMode
                          ? 'bg-[#2D2D2D] text-[#E0E0E0] border border-[#FF8C00]'
                          : 'bg-white text-[#212121] border border-[#FF8C00]'
                      }`}
                    >
                      <option value="python">Python</option>
                      <option value="javascript">JavaScript</option>
                      <option value="java">Java</option>
                      <option value="cpp">C++</option>
                    </select>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex items-center space-x-3">
                    <button
                      onClick={handleRunTestCase}
                      disabled={isLoading || testCases.length === 0}
                      className={`px-4 py-1.5 bg-[#FF8C00] text-white rounded-md hover:bg-[#FF7000] 
                        focus:outline-none focus:ring-2 focus:ring-[#FF8C00] disabled:opacity-50 
                        flex items-center gap-2 ${isLoading ? 'cursor-not-allowed' : ''}`}
                    >
                      <span className="material-icons text-xl">play_arrow</span>
                      Run Test Case
                    </button>

                    <button
                      onClick={handleSubmit}
                      disabled={isLoading}
                      className={`px-4 py-1.5 bg-[#00C853] text-white rounded-md hover:bg-[#00B848] 
                        focus:outline-none focus:ring-2 focus:ring-[#00C853] disabled:opacity-50 
                        flex items-center gap-2 ${isLoading ? 'cursor-not-allowed' : ''}`}
                    >
                      <span className="material-icons text-xl">check_circle</span>
                      {isLoading ? 'Submitting...' : 'Submit'}
                    </button>

                    <button
                      onClick={() => {
                        if (window.confirm('Are you sure you want to end this interview session?')) {
                          console.log('Ending interview session');
                        }
                      }}
                      className={`px-4 py-1.5 bg-red-600 text-white rounded-md hover:bg-red-700 
                        focus:outline-none focus:ring-2 focus:ring-red-500
                        flex items-center gap-2`}
                    >
                      <span className="material-icons text-xl">exit_to_app</span>
                      End Interview
                    </button>
                  </div>
                </div>

                <div className={`h-[calc(100%-3rem)] ${isDarkMode ? 'bg-[#1E1E1E]' : 'bg-white'} rounded-lg shadow-lg`}>
                  <CodeEditor
                    code={code}
                    language={language}
                    onCodeChange={handleCodeChange}
                    isDarkMode={isDarkMode}
                    functionTemplate={functionTemplate}
                  />
                </div>
              </div>

              {/* Test Results Section */}
              <div className="w-full">
                <div className={`h-full ${isDarkMode ? 'bg-[#2D2D2D]' : 'bg-white'} rounded-lg shadow-lg p-4`}>
                  <div className="flex justify-between items-center mb-2">
                    <h2 className={`text-lg font-semibold ${isDarkMode ? 'text-[#E0E0E0]' : 'text-[#212121]'}`}>
                      {output?.isSubmission ? 'Submission Results' : 'Test Results'}
                    </h2>
                  </div>
                  <div className={`h-[calc(100%-2rem)] overflow-auto ${isDarkMode ? 'custom-scrollbar-dark' : 'custom-scrollbar'}`}>
                    {output && (
                      <div className={`${isDarkMode ? 'bg-[#1E1E1E]' : 'bg-[#FAFAFA]'} rounded-md`}>
                        <div className="p-4">
                          {output.error ? (
                            <div className="text-red-500">
                              <div className="font-semibold">Error:</div>
                              <div className="whitespace-pre-wrap">{output.error}</div>
                              {output.details && (
                                <div className="mt-2 text-sm opacity-75 whitespace-pre-wrap">
                                  {output.details}
                                </div>
                              )}
                            </div>
                          ) : (
                            <>
                              {output.isSubmission && output.submissionStatus && (
                                <div className={`mb-4 p-3 rounded ${
                                  output.submissionStatus === 'Accepted'
                                    ? 'bg-[#00C853] bg-opacity-10 text-[#00C853]'
                                    : 'bg-red-500 bg-opacity-10 text-red-500'
                                }`}>
                                  <div className="font-semibold">
                                    Status: {output.submissionStatus}
                                  </div>
                                  {output.runtime && (
                                    <div className={`text-sm ${isDarkMode ? 'text-[#B0B0B0]' : 'text-gray-600'}`}>
                                      Runtime: {output.runtime}ms
                                    </div>
                                  )}
                                  {output.memory && (
                                    <div className={`text-sm ${isDarkMode ? 'text-[#B0B0B0]' : 'text-gray-600'}`}>
                                      Memory: {output.memory}MB
                                    </div>
                                  )}
                                </div>
                              )}
                              {output.stdout && (
                                <div className="mb-2">
                                  <div className={`font-semibold ${isDarkMode ? 'text-[#E0E0E0]' : 'text-gray-800'}`}>
                                    Standard Output:
                                  </div>
                                  <div className={`whitespace-pre-wrap font-mono text-sm ${
                                    isDarkMode ? 'text-[#B0B0B0]' : 'text-gray-600'
                                  }`}>
                                    {output.stdout}
                                  </div>
                                </div>
                              )}
                            </>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </Split>
          </div>
        </Split>
      </main>

      <Footer isDarkMode={isDarkMode} />
    </div>
  );
}

export default App;
