import React, { useState } from 'react';
import { useTheme } from '../hooks/useTheme';
import AddTestCaseModal from './AddTestCaseModal';

const TestCasePanel = ({ testCases, isDarkMode, onAddTestCase, onTestInput }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const { colors } = useTheme(isDarkMode);

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b" style={{ borderColor: colors.border.primary }}>
        <h2 className="text-lg font-medium" style={{ color: colors.text.primary }}>Test Cases</h2>
        <button
          onClick={() => setIsModalOpen(true)}
          className="px-3 py-1 rounded-md text-sm font-medium transition-colors duration-150"
          style={{
            backgroundColor: colors.button.primary.background,
            color: colors.button.primary.text,
          }}
        >
          Add Test Case
        </button>
      </div>

      {/* Test Cases List */}
      <div className="flex-1 overflow-auto p-4">
        {testCases.length === 0 ? (
          <div className="text-center py-8" style={{ color: colors.text.secondary }}>
            No test cases added yet
          </div>
        ) : (
          <div className="space-y-4">
            {testCases.map((testCase, index) => (
              <div
                key={index}
                className="p-4 rounded-md"
                style={{
                  backgroundColor: colors.background.secondary,
                  border: `1px solid ${colors.border.secondary}`,
                }}
              >
                <div className="mb-2">
                  <span className="text-sm font-medium" style={{ color: colors.text.secondary }}>Input:</span>
                  <pre className="mt-1 p-2 rounded text-sm font-mono" style={{ 
                    backgroundColor: colors.background.primary,
                    color: colors.text.primary,
                  }}>
                    {testCase.input}
                  </pre>
                </div>
                <div>
                  <span className="text-sm font-medium" style={{ color: colors.text.secondary }}>Expected Output:</span>
                  <pre className="mt-1 p-2 rounded text-sm font-mono" style={{ 
                    backgroundColor: colors.background.primary,
                    color: colors.text.primary,
                  }}>
                    {testCase.expectedOutput}
                  </pre>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Add Test Case Modal */}
      <AddTestCaseModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAdd={onAddTestCase}
        isDarkMode={isDarkMode}
        onTestInput={onTestInput}
      />
    </div>
  );
};

export default TestCasePanel;
