import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

function ChatBox({ onSendMessage, isDarkMode }) {
  const [messages, setMessages] = useState([
    {
      type: 'bot',
      content: 'Hello! I\'m your AI coding assistant powered by Qwen. How can I help you today?'
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    // Add user message
    const newMessages = [...messages, { type: 'user', content: inputMessage }];
    setMessages(newMessages);
    setInputMessage('');
    setIsLoading(true);

    try {
      // Call backend API
      const response = await axios.post('http://localhost:3001/api/chat', {
        message: inputMessage
      });

      // Add bot response
      setMessages(prev => [...prev, {
        type: 'bot',
        content: response.data.response
      }]);
    } catch (error) {
      console.error('Error:', error);
      // Add error message
      setMessages(prev => [...prev, {
        type: 'bot',
        content: 'Sorry, I encountered an error. Please try again.'
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={`h-full flex flex-col ${isDarkMode ? 'bg-black' : 'bg-white'} rounded-lg shadow`}>
      {/* Chat Header */}
      <div className={`p-4 border-b ${isDarkMode ? 'border-gray-800' : 'border-gray-200'}`}>
        <h2 className={`text-lg font-semibold ${isDarkMode ? 'text-[#FF8C00]' : 'text-gray-800'}`}>
          AI Assistant (Qwen)
        </h2>
      </div>

      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-[80%] rounded-lg px-4 py-2 ${
                message.type === 'user'
                  ? isDarkMode
                    ? 'bg-[#FF8C00] text-black'
                    : 'bg-indigo-500 text-white'
                  : isDarkMode
                  ? 'bg-gray-900 text-[#FF8C00]'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              <p className="whitespace-pre-wrap">{message.content}</p>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <form onSubmit={handleSubmit} className={`p-4 border-t ${isDarkMode ? 'border-gray-800' : 'border-gray-200'}`}>
        <div className="flex space-x-4">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type your message..."
            className={`flex-1 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? 'bg-gray-900 text-[#FF8C00] placeholder-gray-600 focus:ring-[#FF8C00]'
                : 'bg-gray-100 text-gray-800 placeholder-gray-500 focus:ring-indigo-500'
            }`}
            disabled={isLoading}
          />
          <button
            type="submit"
            className={`px-4 py-2 rounded-lg ${
              isDarkMode
                ? 'bg-[#FF8C00] text-black hover:bg-[#FF7000]'
                : 'bg-indigo-600 text-white hover:bg-indigo-700'
            } focus:outline-none focus:ring-2 focus:ring-offset-2 ${
              isDarkMode ? 'focus:ring-[#FF8C00]' : 'focus:ring-indigo-500'
            } ${
              !inputMessage.trim() || isLoading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            disabled={!inputMessage.trim() || isLoading}
          >
            {isLoading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default ChatBox;
