import React, { useState } from 'react';
import Split from 'react-split';
import CodeEditor from './components/CodeEditor';
import ProblemPanel from './components/ProblemPanel';
import Header from './components/Header';
import Footer from './components/Footer';
import './styles/split.css';
import './styles/scrollbar.css';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [output, setOutput] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [testCases, setTestCases] = useState([]);
  const [hiddenTestCases, setHiddenTestCases] = useState([]);
  const [problemDescription, setProblemDescription] = useState('');
  const [functionTemplate, setFunctionTemplate] = useState('');
  const [activeTestCase, setActiveTestCase] = useState(0); // For running individual test cases

  const handleCodeChange = (newCode) => {
    setCode(newCode);
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
    // Update function template based on language
    if (functionTemplate) {
      // This will be handled by the AI to provide language-specific templates
      console.log('Language changed, updating template...');
    }
  };

  const handleChatMessage = async (message) => {
    // This will be implemented with AI integration
    console.log('Chat message:', message);

    // Mock problem setup
    setProblemDescription(`Given an array of integers nums and an integer target, return indices of the two numbers in nums such that they add up to target.
You may assume that each input would have exactly one solution, and you may not use the same element twice.
You can return the answer in any order.

Example:
Input: nums = [2,7,11,15], target = 9
Output: [0,1]
Explanation: Because nums[0] + nums[1] == 9, we return [0, 1].`);

    setTestCases([
      { 
        input: '[2,7,11,15], 9',
        expectedOutput: '[0,1]'
      },
      {
        input: '[3,2,4], 6',
        expectedOutput: '[1,2]'
      }
    ]);

    // Set language-specific function templates
    const templates = {
      python: `def solution(nums, target):
    # Create a hash map to store complements
    seen = {}

    # Iterate through the array
    for i, num in enumerate(nums):
        # Calculate the complement needed
        complement = target - num

        # If complement exists in hash map, we found our pair
        if complement in seen:
            return [seen[complement], i]

        # Store current number and its index
        seen[num] = i

    # No solution found
    return []  # This case won't occur given the problem constraints`,
      javascript: `function solution(nums, target) {
    // Write your code here
}`,
      java: `class Solution {
    public int[] solution(int[] nums, int target) {
        // Write your code here
        return new int[]{};
    }
}`,
      cpp: `class Solution {
public:
    vector<int> solution(vector<int>& nums, int target) {
        // Write your code here
        return {};
    }
};`
    };

    setFunctionTemplate(templates[language]);
  };

  const executeCode = async (testCasesToRun, isSubmission = false) => {
    try {
      setIsLoading(true);
      setOutput(null);

      if (!code || !code.trim()) {
        setOutput({ error: 'Please enter some code to execute' });
        return;
      }

      const response = await fetch('http://localhost:5000/api/code/execute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ 
          code: code.trim(), 
          language,
          testCases: testCasesToRun,
          isSubmission
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.error || `HTTP error! status: ${response.status}`);
      }

      let data;
      try {
        data = await response.json();
      } catch (error) {
        console.error('Failed to parse response:', error);
        throw new Error('Invalid response from server');
      }

      if (!data || typeof data !== 'object') {
        throw new Error('Invalid response format');
      }

      if (!data.success) {
        setOutput({ 
          error: data.error || 'Failed to execute code',
          details: data.details || null
        });
        return;
      }

      setOutput({
        stdout: data.data.stdout || '',
        stderr: data.data.stderr || '',
        error: data.data.error || null,
        testResults: data.data.testResults || [],
        isSubmission: isSubmission,
        submissionStatus: data.data.submissionStatus,
        runtime: data.data.runtime,
        memory: data.data.memory
      });

    } catch (error) {
      console.error('Error running code:', error);
      setOutput({ 
        error: error.message || 'Failed to execute code',
        details: error.details || null
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleRunTestCase = async () => {
    // Run single test case
    const testCase = testCases[activeTestCase];
    await executeCode([testCase]);
  };

  const handleRunAllTestCases = async () => {
    // Run all visible test cases
    await executeCode(testCases);
  };

  const handleSubmit = async () => {
    // Run all test cases including hidden ones
    await executeCode([...testCases, ...hiddenTestCases], true);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`h-screen flex flex-col ${isDarkMode ? 'bg-[#282828]' : 'bg-[#FAFAFA]'}`}>
      <Header isDarkMode={isDarkMode} toggleTheme={toggleTheme} />

      <main className="flex-1 px-4 py-2 sm:px-6 lg:px-8">
        <Split 
          className={`flex h-full ${isDarkMode ? 'dark' : ''}`}
          sizes={[40, 60]} 
          minSize={[300, 400]}
          expandToMin={false}
          gutterSize={10}
          gutterAlign="center"
          snapOffset={30}
          dragInterval={1}
          direction="horizontal"
          cursor="col-resize"
          style={{ position: 'relative' }}
          gutter={(index, direction) => {
            const gutter = document.createElement('div')
            gutter.className = `gutter gutter-${direction}`
            return gutter
          }}
        >
          {/* Left side - Problem Description and Chat */}
          <div className={`h-full ${isDarkMode ? 'bg-[#1a1a1a]' : 'bg-white'} rounded-lg shadow-lg`}>
            <ProblemPanel
              onSendMessage={handleChatMessage}
              isDarkMode={isDarkMode}
              problemDescription={problemDescription}
              testCases={testCases}
            />
          </div>

          {/* Right side - Code Editor and Test Cases */}
          <div className="h-full">
            <Split
              className="flex flex-col h-full"
              sizes={[65, 35]}
              minSize={[200, 100]}
              expandToMin={false}
              gutterSize={10}
              gutterAlign="center"
              snapOffset={30}
              dragInterval={1}
              direction="vertical"
              cursor="row-resize"
              style={{ position: 'relative' }}
              gutter={(index, direction) => {
                const gutter = document.createElement('div')
                gutter.className = `gutter gutter-${direction}`
                return gutter
              }}
            >
              {/* Code Editor Section */}
              <div className="w-full">
                <div className={`flex justify-between items-center mb-2 ${isDarkMode ? 'bg-[#1a1a1a]' : 'bg-white'} p-2 rounded-lg`}>
                  <div className="flex items-center space-x-4">
                    <select
                      value={language}
                      onChange={(e) => handleLanguageChange(e.target.value)}
                      className={`px-3 py-1 rounded-md ${
                        isDarkMode
                          ? 'bg-[#282828] text-[#cccccc] border border-[#404040]'
                          : 'bg-white text-[#262626] border border-gray-300'
                      }`}
                    >
                      <option value="python">Python</option>
                      <option value="javascript">JavaScript</option>
                      <option value="java">Java</option>
                      <option value="cpp">C++</option>
                    </select>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex items-center space-x-3">
                    <button
                      onClick={handleRunTestCase}
                      disabled={isLoading || testCases.length === 0}
                      className={`px-4 py-1.5 bg-[#404040] text-[#cccccc] rounded-md hover:bg-[#4a4a4a] 
                        focus:outline-none focus:ring-2 focus:ring-[#505050] disabled:opacity-50 
                        flex items-center gap-2 ${isLoading ? 'cursor-not-allowed' : ''}`}
                    >
                      <span className="material-icons text-xl">play_arrow</span>
                      Run Test Case
                    </button>

                    <button
                      onClick={handleSubmit}
                      disabled={isLoading}
                      className={`px-4 py-1.5 bg-[#2cbb5d] text-white rounded-md hover:bg-[#2ca854] 
                        focus:outline-none focus:ring-2 focus:ring-[#2cbb5d] disabled:opacity-50 
                        flex items-center gap-2 ${isLoading ? 'cursor-not-allowed' : ''}`}
                    >
                      <span className="material-icons text-xl">check_circle</span>
                      {isLoading ? 'Submitting...' : 'Submit'}
                    </button>

                    <button
                      onClick={() => {
                        if (window.confirm('Are you sure you want to end this interview session?')) {
                          console.log('Ending interview session');
                        }
                      }}
                      className={`px-4 py-1.5 bg-[#ff4d4f] text-white rounded-md hover:bg-[#ff3639] 
                        focus:outline-none focus:ring-2 focus:ring-[#ff4d4f]
                        flex items-center gap-2`}
                    >
                      <span className="material-icons text-xl">exit_to_app</span>
                      End Interview
                    </button>
                  </div>
                </div>

                <div className={`h-[calc(100%-3rem)] ${isDarkMode ? 'bg-[#1a1a1a]' : 'bg-white'} rounded-lg shadow-lg`}>
                  <CodeEditor
                    code={code}
                    language={language}
                    onCodeChange={handleCodeChange}
                    isDarkMode={isDarkMode}
                    functionTemplate={functionTemplate}
                  />
                </div>
              </div>

              {/* Test Results Section */}
              <div className="w-full">
                <div className={`h-full ${isDarkMode ? 'bg-[#1a1a1a]' : 'bg-white'} rounded-lg shadow-lg p-4`}>
                  <div className="flex justify-between items-center mb-2">
                    <h2 className={`text-lg font-semibold ${isDarkMode ? 'text-[#cccccc]' : 'text-[#262626]'}`}>
                      {output?.isSubmission ? 'Submission Results' : 'Test Results'}
                    </h2>
                  </div>
                  <div className={`h-[calc(100%-3rem)] overflow-y-auto custom-scrollbar-dark`}>
                    {output && (
                      <div className={`${isDarkMode ? 'bg-[#282828]' : 'bg-[#f7f7f7]'} rounded-md`}>
                        <div className="p-4">
                          {output.error ? (
                            <div className="text-[#ff4d4f]">
                              <div className="font-semibold">Error:</div>
                              <div className="whitespace-pre-wrap">{output.error}</div>
                              {output.details && (
                                <div className="mt-2 text-sm opacity-75 whitespace-pre-wrap">
                                  {output.details}
                                </div>
                              )}
                            </div>
                          ) : (
                            <>
                              {output.isSubmission && output.submissionStatus && (
                                <div className={`mb-4 p-3 rounded ${
                                  output.submissionStatus === 'Accepted'
                                    ? 'bg-[#2cbb5d] bg-opacity-10 text-[#2cbb5d]'
                                    : 'bg-[#ff4d4f] bg-opacity-10 text-[#ff4d4f]'
                                }`}>
                                  <div className="font-semibold">
                                    Status: {output.submissionStatus}
                                  </div>
                                  {output.runtime && (
                                    <div className={`text-sm ${isDarkMode ? 'text-[#8c8c8c]' : 'text-gray-600'}`}>
                                      Runtime: {output.runtime}ms
                                    </div>
                                  )}
                                  {output.memory && (
                                    <div className={`text-sm ${isDarkMode ? 'text-[#8c8c8c]' : 'text-gray-600'}`}>
                                      Memory: {output.memory}MB
                                    </div>
                                  )}
                                </div>
                              )}
                              {output.stdout && (
                                <div className="mb-2">
                                  <div className={`font-semibold ${isDarkMode ? 'text-[#cccccc]' : 'text-gray-800'}`}>
                                    Standard Output:
                                  </div>
                                  <div className={`whitespace-pre-wrap font-mono text-sm ${
                                    isDarkMode ? 'text-[#8c8c8c]' : 'text-gray-600'
                                  }`}>
                                    {output.stdout}
                                  </div>
                                </div>
                              )}
                              {output.testResults && output.testResults.length > 0 && (
                                <div className="space-y-4">
                                  {output.testResults.map((result, index) => (
                                    <div
                                      key={index}
                                      className={`p-3 rounded ${
                                        result.passed
                                          ? 'bg-[#2cbb5d] bg-opacity-10 text-[#2cbb5d]'
                                          : 'bg-[#ff4d4f] bg-opacity-10 text-[#ff4d4f]'
                                      }`}
                                    >
                                      <div className="font-semibold">
                                        Test Case {index + 1}: {result.passed ? 'Passed' : 'Failed'}
                                      </div>
                                      <div className={`mt-1 text-sm ${isDarkMode ? 'text-[#8c8c8c]' : 'text-gray-600'}`}>
                                        <div>Input: {result.input}</div>
                                        <div>Expected: {result.expectedOutput}</div>
                                        <div>Actual: {result.actualOutput}</div>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              )}
                            </>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </Split>
          </div>
        </Split>
      </main>

      <Footer isDarkMode={isDarkMode} />
    </div>
  );
}

export default App;
